from flask import Flask, render_templatefrom flask.ext.restful import Api, Resourcefrom SystemInfoResource import *app = Flask(__name__)api = Api(app)api.add_resource(MachineInfo, '/system_info/machine_info', endpoint='machine_info')api.add_resource(CpuInfo, '/system_info/cpu_info', endpoint='cpu_info')api.add_resource(MemoryInfo, '/system_info/memory_info', endpoint='memory_info')api.add_resource(DiskInfo, '/system_info/disk_info', endpoint='disk_info')api.add_resource(DiskIOConters, '/system_info/disk_io_counters', endpoint='disk_io_counters')api.add_resource(ProcessInfo, '/system_info/process_info/<int:pid>', endpoint='process_info')api.add_resource(SystemInfo, '/system_info', endpoint='system_info')@app.route("/")@app.route("/index")def getDataStatus():    return render_template("index.html")    if __name__ == '__main__':    app.run(debug=True)